      const stack = state.stack;
      const p2i = stack.pop();
      const p1i = stack.pop();
      const p2 = state.z2[p2i];
      const p1 = state.z1[p1i];

      if (exports.DEBUG) console.log(state.step, 'SDPVTL[' + a + ']', p2i, p1i);

      let dx;
      let dy;

      if (!a) {
          dx = p1.x - p2.x;
          dy = p1.y - p2.y;
      } else {
          dx = p2.y - p1.y;
          dy = p1.x - p2.x;
      }

      state.dpv = getUnitVector(dx, dy);
